if(!window.SA){window.SA={};}SA.redirection_mobile=function(config){var addTimeToDate=function(msec){var exdate=new Date();exdate.setTime(exdate.getTime()+msec);return exdate;};var agent=navigator.userAgent.toLowerCase(),param=config.param||"isStandardSite",TRUE="true",mobile_prefix=config.mobile_prefix||"m",host=document.location.host,mobile_host=mobile_prefix+"."+(!!host.match(/^www\./i)?host.substring(4):host),cookie_hours=config.cookie_hours||1,isUAMobile=!!(agent.match(/iPhone/i)||agent.match(/iPod/i)||agent.match(/iPad/i)||agent.match(/blackberry/i)||agent.match(/android/i));if(document.referrer.indexOf(mobile_host)>=0){if(window.sessionStorage){window.sessionStorage.setItem(param,TRUE);}else{document.cookie=param+"="+TRUE+";expires="+addTimeToDate(3600*1000*cookie_hours).toUTCString();}}var isSessionStorage=(window.sessionStorage)?(window.sessionStorage.getItem(param)===TRUE):false,isCookieSet=document.cookie?(document.cookie.indexOf(param)>=0):false;if(isUAMobile&&!(isCookieSet||isSessionStorage)){document.location.href=document.location.protocol+"//"+mobile_host;}};