window.redirection_mobile=function(document,navigator,object){var agent=navigator.userAgent.toLowerCase(),param=object.param||"isDesktopRedirection=true",mobile_prefix=object.mobile_prefix||"m",search=document.location.search,isUAMobile=!!(agent.match(/iPhone/i)||agent.match(/iPod/i)||agent.match(/blackberry/i)||agent.match(/android/i)),isCookieSet=document.cookie?(document.cookie.indexOf(param)>=0):false,isParameterSet=(search.indexOf(param)>0);var addTimeToDate=function(msec){var exdate=new Date();exdate.setTime(exdate.getTime()+msec);return exdate;};if(isUAMobile&&!isCookieSet&&!isParameterSet){document.location.href=document.location.protocol+"//"+mobile_prefix+"."+document.location.host;}else{if(isParameterSet&&isUAMobile&&!isCookieSet){document.cookie=param+";expires="+addTimeToDate(3600*1000).toUTCString();}}};