window.redirection_mobile=function(document,window,navigator,object){var addTimeToDate=function(msec){var exdate=new Date();exdate.setTime(exdate.getTime()+msec);return exdate;};var agent=navigator.userAgent.toLowerCase(),param=object.param||"isStandardSite",mobile_prefix=object.mobile_prefix||"m",mobile_host=mobile_prefix+"."+document.location.host,isUAMobile=!!(agent.match(/iPhone/i)||agent.match(/iPod/i)||agent.match(/blackberry/i)||agent.match(/android/i));if(document.referrer.indexOf(mobile_host)>=0){if(window.sessionStorage){window.sessionStorage.setItem(param,"true");}else{document.cookie=param+"=true;expires="+addTimeToDate(3600*1000).toUTCString();}}var isSessionStorage=(window.sessionStorage)?(window.sessionStorage.getItem(param)==="true"):false,isCookieSet=document.cookie?(document.cookie.indexOf(param)>=0):false;if(isUAMobile&&!(isCookieSet||isSessionStorage)){document.location.href=document.location.protocol+"//"+mobile_prefix+"."+document.location.host;}};